class AddFriend{constructor(e){this.adder=e,this.addFriend()}addFriend(){$(this.adder).click((function(e){e.preventDefault(),console.log("yeh am clicked");$.ajax({type:"POST",url:$(this).attr("href")}).done((function(e){if(1==e.data.toggle){let d=$(".add-friend");$(" button ",d).html("Un-Friend");var t=$("#delete-friend-"+e.data.friendId);console.log(t),new Noty({theme:"relax",text:"Added to your friend list",type:"success",layout:"topRight",timeout:1500}).show()}else{let d=$(".add-friend");$(" button ",d).html("Add-Friend"),$(".delete-friend-"+e.data.friendId).remove();t=$(".delete-friend-"+e.data.friendId);console.log(t),new Noty({theme:"relax",text:"Removed from your friend list",type:"error",layout:"topRight",timeout:1500}).show()}}))}))}}