class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let l=parseInt($(e).attr("data-likes"));1==t.data.deleted?(l-=1,$(e).html(`<i class="like far fa-lg fa-thumbs-up">${l}</i>`)):(l+=1,$(e).html(`<i class="fas fa-lg fa-thumbs-up">${l}</i>`)),$(e).attr("data-likes",l)})).fail((function(t){console.log("Error in liking post/comment")}))}))}}